import customtkinter as ctk
import requests

class RegistrationScreen(ctk.CTkFrame):
    def __init__(self, master, on_back_click):
        super().__init__(master)

        self.on_back_click = on_back_click

        # Заголовок
        label = ctk.CTkLabel(self, text="Регистрация", font=ctk.CTkFont(size=20, weight="bold"))
        label.pack(pady=12, padx=10)

        # Поле логина
        self.register_login_entry = ctk.CTkEntry(self, placeholder_text="Логин")
        self.register_login_entry.pack(pady=6, padx=10)

        # Поле пароля
        self.register_password_entry = ctk.CTkEntry(self, placeholder_text="Пароль", show="*")
        self.register_password_entry.pack(pady=6, padx=10)

        # Кнопка регистрации
        register_button = ctk.CTkButton(self, text="Зарегистрироваться", command=self.register)
        register_button.pack(pady=12, padx=10)

        # Кнопка возврата на экран входа
        back_button = ctk.CTkButton(self, text="Назад", command=self.on_back_click)
        back_button.pack(pady=12, padx=10)

    def clear_password(self):
        """Очищает поле пароля"""
        self.register_password_entry.delete(0, ctk.END)

    def show_error_window(self, message):
        """Показывает окно с сообщением об ошибке"""
        # Создаем новое окно ошибки
        error_window = ctk.CTkToplevel(self)  # Указываем self (родительский виджет)
        error_window.title("Ошибка")
        parent_x = self.winfo_rootx()
        parent_y = self.winfo_rooty()
        parent_width = self.winfo_width()
        parent_height = self.winfo_height()

        window_width = 300
        window_height = 100

        x = parent_x + (parent_width // 2) - (window_width // 2)
        y = parent_y + (parent_height // 2) - (window_height // 2)

        error_window.geometry(f"{window_width}x{window_height}+{x}+{y}")

        # Делаем окно модальным
        error_window.grab_set()  # Захватываем фокус
        error_window.focus_force()  # Принудительно переключаем фокус на это окно

        # Метка с текстом ошибки
        error_label = ctk.CTkLabel(error_window, text=message, text_color="red", wraplength=250)
        error_label.pack(pady=10, padx=10)

        # Кнопка закрытия окна
        ok_button = ctk.CTkButton(error_window, text="OK", command=error_window.destroy)
        ok_button.pack(pady=10)

        # Устанавливаем положение окна ошибки по центру относительно родительского окна


    def register(self):
        """Обработка регистрации"""
        login = self.register_login_entry.get()
        password = self.register_password_entry.get()

        try:
            response = requests.post("http://127.0.0.1:5000/register", json={"nickname": login, "password": password, "name": login, "avatar": ""})
            if response.status_code == 200:
                self.master.show_main_menu(login)  # Переход к главному меню
            else:
                error_message = response.json().get("error", "Неизвестная ошибка")
                self.show_error_window(error_message)  # Показываем окно с ошибкой
                self.clear_password()  # Очищаем поле пароля
        except Exception as e:
            self.show_error_window(f"Ошибка соединения с сервером")
            self.clear_password()  # Очищаем поле пароля